Employee.getEmployeeByID = (id, result) => {
  db.query("SELECT * FROM employee where employeeID = ?", id, (err, res) => {
    if (err) {
      console.log("Error while fetching employee by id ", err)
      result(null, err)
    } else {
      result(null, res)
    }
  })
}

