  db.query("INSERT INTO employee SET ? ", employeeReqData, (err, res) => {
    if (err) {
      console.log("Error while inserting data ")
      result(null, err)
    } else {
      console.log("Employee Created Successfully")
      result(null, res)
    }
  })
}

